        {5, "Item 5", 5.5},
        {3, "Item 3", 1.1}
    };

    char choice;

    do {
        cout << "Menu:\n";
        cout << "1. QuickSort\n";
        cout << "2. ShellSort\n";
        cout << "3. Bubble Sort\n";
        cout << "4. Save to file\n";
        cout << "5. Display data\n";
        cout << "6. Exit\n";
        cout << "Choose an option: ";
        cin >> choice;

        switch (choice) {
            case '1':
                quickSort(data, 0, data.size() - 1);
                break;
            case '2':
                shellSort(data);
                break;
            case '3':
                bubbleSort(data);
                break;
            case '4':
                saveToFile(data, "output.txt");
                break;
            case '5':
                displayData(data);
                break;
            case '6':
                break;
            default:
                cout << "Invalid option. Please try again.\n";
                break;
        }
    } while (choice != '6');

    return 0;
}
