                byte[] messageDigest = digest.getMessageDigest().digest(IOUtils.toByteArray(data));
                PrivateKey privateKey = signature.getPrivateKey();
                java.security.Signature jcaSignature = java.security.Signature.getInstance(signature.getAlgorithm());
                jcaSignature.initSign(privateKey);
                jcaSignature.update(messageDigest);
                return jcaSignature.sign();
            } catch (Exception e) {
                throw new SignatureException(e);
            }
        }

        @Override
        public void modifySignatureDictionary(PdfDictionary signDic) {
            signDic.put(PdfName.NAME, new PdfString(certAlias));
        }

        @Override
