#include <vector>
#include <iostream>
#include <fstream>
#include <cstdint>
#include <cmath>

#include "gb28181_demux.h"

#include <asio/io_context.hpp>
#include <asio/ip/udp.hpp>

class rtp_header {
public:
    uint8_t cc() const { return header_[0] & 0x0F; }
    uint16_t seq() const { return ntohs(*(reinterpret_cast<const uint16_t*>(&header_[2]))); }
    uint32_t timestamp() const { return ntohl(*(reinterpret_cast<const uint32_t*>(&header_[4]))); }
    uint32_t ssrc() const { return ntohl(*(reinterpret_cast<const uint32_t*>(&header_[8]))); }
    const uint8_t* data() const { return header_; }
    size_t size() const { return 12 + cc() * 4; }

private:
    uint8_t header_[12] = { 0x80, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00 };
};

void receive_rtp_packet(asio::ip::udp::socket& socket, std::vector<uint8_t>& buffer) {
    asio::ip::udp::endpoint remote_endpoint;
    socket.receive_from(asio::buffer(buffer), remote_endpoint);
}

int main()
{
    uint16_t port = 5004;
    asio::io_context io_context;

    asio::ip::udp::socket rtp_socket(io_context, asio::ip::udp::endpoint(asio::ip::udp::v4(), port));
    std::vector<uint8_t> rtp_packet_buffer(4096);

    std::ofstream h264_out("h264.h264", std::ios::binary | std::ios::out);
    std::ofstream aac_out("aac.aac", std::ios::binary | std::ios::out);

    GB28181_demux demux;

    while (true) {
        receive_rtp_packet(rtp_socket, rtp_packet_buffer);
        rtp_header header;

        const uint8_t* payload = rtp_packet_buffer.data() + header.size();
        size_t payload_size = rtp_packet_buffer.size() - header.size();
        uint32_t payload_type = rtp_packet_buffer[1] & 0x7F;

        if (payload_type == 96 /* GB28181 PSæµ */) {
            demux.push_ps_packet(payload, payload_size);

            while (true) {
                FrameType type;
                const uint8_t* frame_data;
                size_t frame_size;

                if (!demux.get_frame(&type, &frame_data, &frame_size)) {
                    break;
                }

                if (type == FrameType::H264) {
                    h264_out.write(reinterpret_cast<const char*>(frame_data), frame_size);
                } else if (type == FrameType::AAC) {
                    aac_out.write(reinterpret_cast<const char*>(frame_data), frame_size);
                }
            }
        }
    }

    h264_out.close();
    aac_out.close();

    return 0;
}
```

