// æ ¹æ®xè½´ã€yè½´ã€zè½´å¯¹ç‰©ä½“è¿›è¡Œæ’åº
bool BoxCompare(const Hittable* a, const Hittable* b, int axis)
{
    AABB boxA = Box(*a, 0, 0);
    AABB boxB = Box(*b, 0, 0);
    return boxA.Min().e[axis] < boxB.Min().e[axis];
}

// æ„é€ BVHæ ‘ç»“æ„
class BVHNode : public Hittable {
public:
    BVHNode() {}
    BVHNode(Hittable** list, int n, float time0, float time1);
    virtual bool Hit(const Ray& r, float tmin, float tmax, HitRecord& rec) const;
    virtual bool BoundingBox(float t0, float t1, AABB& output_box) const;

public:
    Hittable* left;
    Hittable* right;
    AABB box;
};

// æ„é€ BVHæ ‘ç»“æ„
BVHNode::BVHNode(Hittable** list, int n, float time0, float time1)
{
    int axis = int(3 * drand48()); // éšæœºæ’åºæ–¹å‘
    std::sort(list, list + n, [axis](Hittable* a, Hittable* b)
    {
        return BoxCompare(a, b, axis);
    });

    if (n == 1) { // åªæœ‰ä¸€ä¸ªç‰©ä½“
        left = right = list[0];
    }
    else if (n == 2) { // ä¸¤ä¸ªç‰©ä½“
        left = list[0];
        right = list[1];
    }
    else { // å¤šä¸ªç‰©ä½“
        int mid = n / 2;
        left = new BVHNode(list, mid, time0, time1);
        right = new BVHNode(list + mid, n - mid, time0, time1);
    }

    AABB boxLeft = Box(*left, time0, time1);
    AABB boxRight = Box(*right, time0, time1);
    box = SurroundingBox(boxLeft, boxRight); // è®¡ç®—æ‰€æœ‰ç‰©ä½“çš„AABBç›’å­
}

// åˆ¤æ–­BVHèŠ‚ç‚¹æ˜¯å¦è¢«å…‰çº¿å‡»ä¸­
bool BVHNode::Hit(const Ray& r, float t_min, float t_max, HitRecord& rec) const
{
    if (!box.Hit(r, t_min, t_max))
